mapper.select:

select 

from ATY_ACTIVITY A
where A.ACTIVITY_ID = #{activityId,jdbcType=DECIMAL}
  and A.TYPE = #{type,jdbcType=CHAR}

mapper.delete:

delete from ATY_ACTIVITY
where ACTIVITY_ID = #{activityId,jdbcType=DECIMAL}
  and TYPE = #{type,jdbcType=CHAR}

mapper.select:
SELECT COUNT(A.ACTIVITY_ID) FROM ATY_ACTIVITY A LEFT JOIN ATY_ENROLL_LEARNER E ON A.ACTIVITY_ID=E.ACTIVITY_ID and E.User_Id=#{params.userId,jdbcType=DECIMAL} and E.Record_Status='A' LEFT JOIN ATY_USER_ACTIVITY F ON A.ACTIVITY_ID=F.ACTIVITY_ID and F.USER_ID=#{params.userId,jdbcType=DECIMAL} and F.Record_Status='A' where A.FOLDER_ID IN(SELECT C.ID FROM ATY_FOLDER C WHERE C.SITE_ID=#{params.siteId,jdbcType=DECIMAL}) AND A.RELEASE_STAUTS ='R' AND A.TYPE=#{params.flagType,jdbcType=VARCHAR} AND A.RECORD_STATUS='A' AND A.RELEASE_DATE > SYSDATE-#{params.newest,jdbcType=DECIMAL}
AND A.NAME like #{params.activityFindName}
AND TO_CHAR(A.RELEASE_DATE) IS NOT NULL ORDER BY A.RELEASE_DATE DESC

select IFNULL(F.STATUS,'N') F_STATUS, IFNULL(E.Stauts,'O') E_STATUS, E.ENROLL_MODE ENROLL_MODE,
FROM ATY_ACTIVITY A LEFT JOIN ATY_ENROLL_LEARNER E ON A.ACTIVITY_ID=E.ACTIVITY_ID and E.User_Id=#{params.userId,jdbcType=DECIMAL} and E.Record_Status='A' LEFT JOIN ATY_USER_ACTIVITY F ON A.ACTIVITY_ID=F.ACTIVITY_ID and F.USER_ID=#{params.userId,jdbcType=DECIMAL} and F.Record_Status='A' WHERE A.FOLDER_ID IN (SELECT C.ID FROM ATY_FOLDER C WHERE C.SITE_ID=#{params.siteId,jdbcType=DECIMAL}) AND A.RELEASE_STAUTS ='R' AND A.TYPE=#{params.flagType,jdbcType=VARCHAR} AND A.RECORD_STATUS='A' AND A.RELEASE_DATE > SYSDATE-#{params.newest,jdbcType=DECIMAL}
AND A.NAME like #{params.activityFindName}
AND TO_CHAR(A.RELEASE_DATE) IS NOT NULL ORDER BY A.RELEASE_DATE DESC

SELECT COUNT(A.ACTIVITY_ID) FROM ATY_ACTIVITY A LEFT JOIN ATY_ENROLL_LEARNER E ON A.ACTIVITY_ID=E.ACTIVITY_ID AND E.USER_ID=#{params.userId,jdbcType=DECIMAL} AND E.RECORD_STATUS='A' LEFT JOIN ATY_USER_ACTIVITY F ON A.ACTIVITY_ID = F.ACTIVITY_ID and F.USER_ID = #{params.userId,jdbcType=DECIMAL} AND F.RECORD_STATUS='A', aty_elearning D,crs_course_info G, (select C.activity_id activity_id,count(C.id) p_number from aty_user_activity C group by C.activity_id) B WHERE A.ACTIVITY_ID=B.ACTIVITY_ID AND A.TYPE=#{params.flagType,jdbcType=VARCHAR} AND A.RELEASE_STAUTS ='R' AND A.ACTIVITY_ID=D.ID AND G.ID=D.COURSE_ID AND A.RECORD_STATUS='A' AND A.RELEASE_DATE > SYSDATE-#{params.popular,jdbcType=DECIMAL}
AND A.NAME like #{params.activityFindName}
AND A.FOLDER_ID IN ( SELECT C.ID FROM ATY_FOLDER C WHERE C.SITE_ID=#{params.siteId,jdbcType=DECIMAL} ) ORDER BY B.P_NUMBER DESC,A.GRADE DESC

select IFNULL(F.STATUS,'N') F_STATUS, IFNULL(E.Stauts,'O') E_STATUS, E.ENROLL_MODE ENROLL_MODE, , B.P_NUMBER AS B_P_NUMBER,G.ATTACHMENT_ID AS G_ATTACHMENT_ID FROM ATY_ACTIVITY A LEFT JOIN ATY_ENROLL_LEARNER E ON A.ACTIVITY_ID=E.ACTIVITY_ID AND E.USER_ID=#{params.userId,jdbcType=DECIMAL} AND E.RECORD_STATUS='A' LEFT JOIN ATY_USER_ACTIVITY F ON A.ACTIVITY_ID = F.ACTIVITY_ID and F.USER_ID = #{params.userId,jdbcType=DECIMAL} AND F.RECORD_STATUS='A', aty_elearning D,crs_course_info G, (select C.activity_id activity_id,count(C.id) p_number from aty_user_activity C group by C.activity_id) B WHERE A.ACTIVITY_ID=B.ACTIVITY_ID AND A.TYPE=#{params.flagType,jdbcType=VARCHAR} AND A.RELEASE_STAUTS ='R' AND A.ACTIVITY_ID=D.ID AND G.ID=D.COURSE_ID AND A.RECORD_STATUS='A' AND A.RELEASE_DATE > SYSDATE-#{params.popular,jdbcType=DECIMAL}
AND A.NAME like #{params.activityFindName} escape '/'
AND A.FOLDER_ID IN ( SELECT C.ID FROM ATY_FOLDER C WHERE C.SITE_ID=#{params.siteId,jdbcType=DECIMAL} ) ORDER BY B.P_NUMBER DESC,A.GRADE DESC
