mapper.select:

select 

from CRS_COURSE_INFO A
where A.ID = #{id,jdbcType=DECIMAL}


select

 from CRS_COURSE_INFO A INNER JOIN CMN_OBJECT_RELATION B ON A.ID = B.OBJECT1_ID 



	  and
	B.RECORD_STATUS='A'
	AND A.RECORD_STATUS='A'

	AND B.OBJECT2_ID = #{params.categoryId,jdbcType=DECIMAL} 


	AND B.OBJECT2_TYPE = #{params.category,jdbcType=CHAR}





  order by

    ${orderBy}

select count(A.ID) from CRS_COURSE_INFO A INNER JOIN CMN_OBJECT_RELATION B ON A.ID = B.OBJECT1_ID
and B.RECORD_STATUS='A' AND A.RECORD_STATUS='A'
AND B.OBJECT2_ID = #{params.categoryId,jdbcType=DECIMAL}
AND B.OBJECT2_TYPE = #{params.category,jdbcType=CHAR}
