mapper.select:

    select

     from ORG_ORGANIZATION A 

     inner join () B on A.ID = B.ENTITY_ID 



      	A.ID IN (
                select id from ORG_ORGANIZATION start with id in (
                  select id from (select id,name from ORG_ORGANIZATION start with id =#{params.organizationId,jdbcType=DECIMAL} connect by prior id=parent_id ) a   

                  	where name like #{params.foldeNameKeyword} escape '\' 


                   AND A.TYPE=#{params.treeType}

                  )
                  connect by prior parent_id=id
        ) AND


        A.ID IN (
            select distinct id from ORG_ORGANIZATION start with id in (
				select A.ID as A_ID 
     			from ORG_ORGANIZATION A 
     			where A.name like #{params.foldeNameKeyword} escape '\' 

                   AND A.TYPE=#{params.treeType}

			) connect by prior parent_id=id
        ) AND





      order by

        ${orderBy}


select

 from ORG_ORGANIZATION A INNER JOIN ORG_ORGANIZATION_HIERARCHY B on A.ID = B.HIERARCHY_ID AND A.RECORD_STATUS = 'A' WHERE B.ORGANIZATION_ID = #{params.organizationID}

  order by

    ${orderBy}
