mapper.select:

select 

from ORG_USER_PROPERTIES A
where A.ID = #{id,jdbcType=DECIMAL}


select  IFNULL(SUBSTRING(A.PROPERTY_NAME, 9),0)-0  AS PROPERTY_NUMBER from ORG_USER_PROPERTIES A 
where A.SITE_ID = #{siteId,jdbcType=DECIMAL}
order by PROPERTY_NUMBER

SELECT * FROM (SELECT A.* FROM ORG_USER_PROPERTIES A WHERE A.RECORD_STATUS = 'A' AND A.SITE_ID = #{siteId,jdbcType=DECIMAL}
AND A.SEQ < #{seq,jdbcType=DECIMAL} order by SEQ desc
AND A.SEQ > #{seq,jdbcType=DECIMAL} order by SEQ
AND A.SEQ < #{seq,jdbcType=DECIMAL} order by SEQ
AND A.SEQ > #{seq,jdbcType=DECIMAL} order by SEQ desc
) where ROWNUM = 1 AND RECORD_STATUS = 'A'

update.if:

update ORG_USER_PROPERTIES A SET A.SEQ = SEQ + #{seq,jdbcType=DECIMAL} where A.ID <> #{id,jdbcType=DECIMAL}
   AND A.SEQ < #{newGroupSeq,jdbcType=DECIMAL} 
   AND A.SITE_ID = #{siteId,jdbcType=DECIMAL}


update ORG_USER_PROPERTIES A SET A.SEQ = SEQ - #{seq,jdbcType=DECIMAL} WHERE A.ID <> #{id,jdbcType=DECIMAL}
AND A.SEQ > #{newGroupSeq,jdbcType=DECIMAL}
AND A.SITE_ID = #{siteId,jdbcType=DECIMAL}
