mapper.select:
select ,C.MAX_SCORE AS C_MAX_SCORE, C.MIN_SCORE AS C_MIN_SCORE, C.AVG_SCORE AS C_AVG_SCORE, (ROUND(G.PASS_NUMBER/H.ALL_NUMBER,2))*100 AS PASSING_RATE FROM RES_SCORE_SHEET A LEFT JOIN (SELECT B.SCORE_SHEET_ID, MAX(B.SCORE1+B.SCORE2+B.SCORE3+B.SCORE4+B.SCORE5+B.SCORE6+B.SCORE7+B.SCORE8+B.SCORE9+B.SCORE10) MAX_SCORE, MIN(B.SCORE1+B.SCORE2+B.SCORE3+B.SCORE4+B.SCORE5+B.SCORE6+B.SCORE7+B.SCORE8+B.SCORE9+B.SCORE10) MIN_SCORE, AVG(B.SCORE1+B.SCORE2+B.SCORE3+B.SCORE4+B.SCORE5+B.SCORE6+B.SCORE7+B.SCORE8+B.SCORE9+B.SCORE10) AVG_SCORE FROM RES_LEARNER_SCORE B GROUP BY B.SCORE_SHEET_ID) C ON C.SCORE_SHEET_ID=A.ID LEFT JOIN (SELECT E.SCORE_SHEET_ID AS SCORE_SHEET_ID,COUNT(1) AS PASS_NUMBER FROM ( SELECT D.SCORE_SHEET_ID, (D.SCORE1+D.SCORE2+D.SCORE3+D.SCORE4+D.SCORE5+D.SCORE6+D.SCORE7+D.SCORE8+D.SCORE9+D.SCORE10 ) AS SUM_SCORE_ONE_PORSON FROM RES_LEARNER_SCORE D
) E WHERE E.SUM_SCORE_ONE_PORSON > ( SELECT F.PASSSCORE FROM RES_SCORE_SHEET F WHERE E.SCORE_SHEET_ID= F.ID )GROUP BY E.SCORE_SHEET_ID ) G ON G.SCORE_SHEET_ID=A.ID LEFT JOIN (SELECT I.SCORE_SHEET_ID, COUNT(1) AS ALL_NUMBER FROM RES_LEARNER_SCORE I, RES_SCORE_SHEET J WHERE I.SCORE_SHEET_ID=J.ID GROUP BY I.SCORE_SHEET_ID) H ON H.SCORE_SHEET_ID=A.ID
order by
${orderBy}
